pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "./Uniqys.sol";

/**
 * @title Stakable
 * @dev ERC20 stake logic
 */
contract Stakable is ERC20 {
    using SafeMath for uint256;

    Uniqys.ValidatorSet public validatorSet;
    uint256 totalPower = 0;

    event StakeUpdate(address indexed account, uint256 power);

    constructor () public {
    }

    /**
     * @dev Function to deposit tokens to stake
     * @param _power The amount of tokens to deposit.
     * @return A boolean that indicates if the operation was successful.
     */
    function stakeDeposit(uint256 _power) public returns (bool) {
        _stakeDeposit(msg.sender, _power);
        return true;
    }

    function _stakeDeposit(address _account, uint256 _power) internal {
        require(balanceOf(_account) >= _power, "not enough balance");
        _burn(_account, _power);
        if (validatorSet.powers[_account] == 0) validatorSet.validators.push(_account);
        validatorSet.powers[_account] = validatorSet.powers[_account].add(_power);
        validatorSet.totalPower = validatorSet.totalPower.add(_power);
        emit StakeUpdate(_account, validatorSet.powers[_account]);
    }

    /**
     * @dev Function to withdraw tokens from stake
     * @param _power The amount of tokens to withdraw.
     * @return A boolean that indicates if the operation was successful.
     */
    function stakeWithdraw(uint256 _power) public returns (bool) {
        _stakeWithdraw(msg.sender, _power);
        return true;
    }

    function _stakeWithdraw(address _account, uint256 _power) internal {
        require(validatorSet.powers[_account] >= _power, "not enough stake");
        _mint(_account, _power);
        validatorSet.powers[_account] = validatorSet.powers[_account].sub(_power);
        if (validatorSet.powers[_account] == 0) {
            /*
            uint256 validatorCount = validatorSet.validators.length;
            // TODO: fix slash logic
            address[] storage _newValidators;
            for (uint i = 0; i < validatorCount; i++) {
                if (validatorSet.validators[i] != _account) _newValidators.push(validatorSet.validators[i]);
            }
            validatorSet.validators = _newValidators;
            */
        }
        validatorSet.totalPower = validatorSet.totalPower.sub(_power);
        emit StakeUpdate(_account, validatorSet.powers[_account]);
    }
}

